INSERT INTO announcements (announcement_id, title, content, created_by, created_at) VALUES
(1, 'Welcome to SABTRACK', 'Welcome to the SABTRACK waste management system. Please report any waste-related issues through our platform.', 1, '2024-01-01 08:00:00'),
(2, 'Waste Collection Schedule Update', 'Please note that waste collection schedules may vary due to holidays. Check the schedule page for updates.', 1, '2024-01-02 09:00:00'),
(3, 'Environmental Awareness Campaign', 'Join our environmental awareness campaign to keep Barangay Sabang clean and green.', 1, '2024-01-03 10:00:00'),
(4, 'New Recycling Program', 'We are introducing a new recycling program. Separate your recyclables for better waste management.', 1, '2024-01-04 11:00:00'),
(5, 'Proper Waste Disposal Tips', 'Learn how to properly segregate your waste into biodegradable, non-biodegradable, and recyclable categories to improve waste management.', 1, '2024-01-05 12:00:00'),
(6, 'Community Clean-Up Event', 'Join us for a community clean-up event this Saturday at the barangay hall. Let''s work together to keep our environment clean.', 1, '2024-01-06 13:00:00'),
(7, 'Hazardous Waste Handling', 'Guidelines for handling hazardous waste: Do not mix with regular trash. Contact authorities for proper disposal methods.', 1, '2024-01-07 14:00:00'),
(8, 'Reduce, Reuse, Recycle Initiative', 'Embrace the 3Rs: Reduce consumption, Reuse items, and Recycle materials to minimize waste and protect the planet.', 1, '2024-01-08 15:00:00'),
(9, 'Waste Management Workshop', 'Attend our upcoming workshop on effective waste management techniques. Register now to learn valuable skills.', 1, '2024-01-09 16:00:00'),
(10, 'Plastic Pollution Awareness', 'Plastic pollution is a growing threat. Reduce single-use plastics and opt for eco-friendly alternatives.', 1, '2024-01-10 17:00:00'),
(11, 'Composting at Home', 'Start composting your organic waste at home. It''s easy, beneficial for the soil, and reduces landfill contributions.', 1, '2024-01-11 18:00:00'),
(12, 'Illegal Dumping Penalties', 'Illegal dumping of waste is prohibited and may result in fines. Help us maintain a clean community by reporting violations.', 1, '2024-01-12 19:00:00'),
(13, 'Eco-Friendly Products', 'Discover eco-friendly products available in our barangay. Support sustainable businesses and reduce environmental impact.', 1, '2024-01-13 20:00:00'),
(14, 'Waste Collection Efficiency', 'Our waste collection services are now more efficient. Expect timely pickups and better coverage in all areas.', 1, '2024-01-14 21:00:00'),
(15, 'Green Living Tips', 'Adopt green living habits: Conserve water, save energy, and minimize waste for a healthier planet.', 1, '2024-01-15 22:00:00'),
(16, 'Recycling Centers Locations', 'Find recycling centers near you. Drop off your recyclables and contribute to a circular economy.', 1, '2024-01-16 23:00:00'),
(17, 'Seasonal Waste Changes', 'During the rainy season, waste collection may be affected. Plan accordingly and store waste securely.', 1, '2024-01-17 08:00:00'),
(18, 'Volunteer Opportunities', 'Volunteer for environmental initiatives. Join clean-up drives and awareness programs in your community.', 1, '2024-01-18 09:00:00'),
(19, 'Waste Audit Results', 'Our recent waste audit shows progress in recycling rates. Thank you for your participation in sustainable practices.', 1, '2024-01-19 10:00:00'),
(20, 'Sustainable Packaging', 'Choose products with sustainable packaging. Reduce plastic waste by selecting biodegradable or reusable options.', 1, '2024-01-20 11:00:00');
